const reducer = (posts = [], action) => {
  switch (action.type) {
	  case "UPDATE":
		  return posts.map((post) => post._id === action.payload._id )
    case "FETCH_ALL":
      return action.posts;
    case "CREATE":
      return [...posts, action.payload];
    default:
      return posts;
  }
};
export default reducer
